<?php ob_start();require_once("../../config/sessions.php");require_once(__DIR__ . "/../../model/Petition.php"); require_once(__DIR__ . "/../../model/Status.php"); include('pdf_mc_table.php');$petitionModel = new Petition();$statusModel = new Status();    if($_GET){        $postDetails = $_GET;               // $startTime = $postDetails['from']?$postDetails['from']:null;        // $endTime = $postDetails['to']?$postDetails['to']:null;        $userId = $postDetails['userId']?$postDetails['userId']:null;           $committee = isset($postDetails['committee'])?$postDetails['committee']:null;       }    if($_POST){        $postDetails = $_POST;               $startTime = $postDetails['from']?$postDetails['from']:null;        $endTime = $postDetails['to']?$postDetails['to']:null;        $userId = $postDetails['userId']?$postDetails['userId']:null;                      }    $searchResults = $petitionModel->getReportPetition($startTime, $endTime, $userId,$committee);        $pdf = new PDF_MC_Table();    $pdf->AliasNbPages();        //Add page and set font    $pdf->AddPage('P');    $pdf->SetFont('Times','B',10);    //Set width for each column    $pdf->SetWidths(Array(10,30,50,70,30));    $pdf->SetLineHeight(7);        $pdf->Cell(10,10,'',0,0,'L',false);    $pdf->Cell(30,10,'Date',0,0,'L',false);    $pdf->Cell(50,10,'Petitioner',0,0,'L',false);    $pdf->Cell(70,10,'Subject',0,0,'L',false);    $pdf->Cell(30,10,'Status',0,1,'L',false);            // $this->Ln(20);    $pdf->SetFont('Times','',10);       $rowNumber = 1;    foreach($searchResults as $result){        $status = $statusModel->getStatusName($result['status']);        $pdf->Row(Array(            $rowNumber,            date("Y-m-d", strtotime($result['dateCreated'])),            $result['petitioner'],             $result['subject'],            $status['name'],        ));        $rowNumber++;    }ob_end_clean();$pdf->Output(); ob_get_flush();    ?>